mapper.select:
SELECT pcxflfbm,pcxflfmc,pcxflbm,pcxflmc,pcxbm,pcxmc,nvl(jb.wts,0) wts FROM (SELECT x.fflxtdm pcxflfbm,d2.mc pcxflfmc, x.flxtdm pcxflbm,d1.mc pcxflmc,x.xtdm pcxbm,d.mc pcxmc FROM xt_pc_pcx x INNER JOIN xt_pc_dm d ON x.xtdm=d.dm and d.sfsc = 'N' INNER JOIN xt_pc_dm d1 ON x.flxtdm=d1.dm and d.sfsc = 'N' INNER JOIN xt_pc_dm d2 ON x.fflxtdm=d2.dm and d.sfsc = 'N' WHERE x.pcflbm='001'
AND x.flxtdm=#{flxtdm}
AND x.ywtx = #{ywtx}
) PCX_XTDM LEFT JOIN (SELECT pcx.fflxtdm,pcx.flxtdm,pcx.xtdm,COUNT(pcslbm) wts FROM ( SELECT onx.ywtx,onx.pcxbm,onx.pcslbm,onx.xtdm,onx.pcjg,onx.fflxtdm,onx.flxtdm FROM yx_pc_pcx onx WHERE onx.pcjg != '0'
AND onx.flxtdm=#{flxtdm}
AND onx.ywtx = #{ywtx}
UNION ALL SELECT ofx.ywtx,ofx.pcxbm,ofx.pcslbm,ofx.xtdm,ofx.pcjg,ofx.fflxtdm,ofx.flxtdm FROM yx_pc_offline_pcx ofx WHERE ofx.pcjg != '0'
AND ofx.flxtdm=#{flxtdm}
AND ofx.ywtx = #{ywtx}
) pcx WHERE EXISTS ( SELECT 1 FROM v_pcajxx jb
AND pcx.pcslbm = jb.PCSLBM ) GROUP BY pcx.fflxtdm,pcx.flxtdm,pcx.xtdm ) jb ON jb.xtdm = PCX_XTDM.pcxbm AND jb.fflxtdm = PCX_XTDM.pcxflfbm AND jb.flxtdm = PCX_XTDM.pcxflbm ORDER by PCX_XTDM.pcxflfbm,PCX_XTDM.pcxflbm,decode(PCX_XTDM.pcxbm,'40000','999999',PCX_XTDM.pcxbm)

 select * from(
 select PCSLBM,BMSAH,AJMC,AJLBMC, CBDWMC, CBRMC, WCRQ,PCFLBM,CJSJ, CBDWBM，rownum rn from(
 select distinct PCSLBM,BMSAH,AJMC,AJLBMC, CBDWMC, CBRMC, WCRQ,PCFLBM,CJSJ, CBDWBM from(
 select distinct jb.PCSLBM,jb.BMSAH,jb.AJMC,jb.AJLB_MC AJLBMC,jb.BPC_DWMC CBDWMC,jb.PCR_MC CBRMC,jb.BPC_MC BCBRMC,jb.BPC_WCRQ
 WCRQ,jb.PCFLBM,jb.CJSJ,jb.BPC_DWBM CBDWBM from v_pcajxx jb
 left join yx_pc_pcx x on x.pcslbm=jb.pcslbm


     and x.fflxtdm=#{fflxtdm}


     and x.flxtdm=#{flxtdm}


     and x.xtdm=#{xtdm}

 group by jb.pcslbm,jb.PCSLBM,jb.BMSAH,jb.AJMC,jb.AJLB_MC,jb.BPC_DWMC,jb.PCR_MC,jb.BPC_MC,jb.BPC_WCRQ,jb.PCFLBM,CJSJ,jb.BPC_DWBM
 union all
 select distinct jb.PCSLBM,jb.BMSAH,jb.AJMC,jb.AJLB_MC AJLBMC,jb.BPC_DWMC CBDWMC,jb.PCR_MC CBRMC,jb.BPC_MC BCBRMC,jb.BPC_WCRQ WCRQ,
 jb.PCFLBM,CJSJ,jb.BPC_DWBM CBDWBM from v_pcajxx jb
 left join yx_pc_offline_pcx x on x.pcslbm=jb.pcslbm


     and x.fflxtdm=#{fflxtdm}


     and x.flxtdm=#{flxtdm}


     and x.xtdm=#{xtdm}

group by jb.pcslbm,jb.pcslbm,jb.PCSLBM,jb.BMSAH,jb.AJMC,jb.AJLB_MC,jb.BPC_DWMC,jb.PCR_MC,jb.BPC_MC,jb.BPC_WCRQ,jb.PCFLBM,CJSJ,jb.BPC_DWBM
 )) where rownum <= #{row} group by PCSLBM,BMSAH,AJMC,AJLBMC, CBDWMC, CBRMC, WCRQ,PCFLBM,CJSJ, CBDWBM,rownum
 ) where rn>#{page}

select count(distinct pcslbm) num from(
select distinct jb.pcslbm from v_pcajxx jb
left join yx_pc_pcx x on x.pcslbm=jb.pcslbm


    and x.fflxtdm=#{fflxtdm}


    and x.flxtdm=#{flxtdm}


    and x.xtdm=#{xtdm}

 group by jb.pcslbm
union all
select distinct jb.pcslbm from v_pcajxx jb
left join yx_pc_offline_pcx x on x.pcslbm=jb.pcslbm


    and x.fflxtdm=#{fflxtdm}


    and x.flxtdm=#{flxtdm}


    and x.xtdm=#{xtdm}

group by jb.pcslbm
)
