mapper.select:
SELECT pcxflfbm,pcxflfmc,pcxflbm,pcxflmc,pcxbm,pcxmc,sum(nvl(wts,0)) wts from (SELECT DISTINCT x.fflxtdm pcxflfbm,d2.mc pcxflfmc, x.flxtdm pcxflbm,d1.mc pcxflmc,x.xtdm pcxbm,d.mc pcxmc from xt_pc_pcx x INNER JOIN xt_pc_dm d ON x.xtdm=d.dm and d.sfsc = 'N' INNER JOIN xt_pc_dm d1 ON x.flxtdm=d1.dm and d.sfsc = 'N' INNER JOIN xt_pc_dm d2 ON x.fflxtdm=d2.dm and d.sfsc = 'N'order by x.fflxtdm, x.flxtdm,x.xtdm) PCX_XTDM left join (select xtdm,flxtdm,fflxtdm,sum(nvl(wts,0)) wts from ( select * from (SELECT x.xtdm,x.flxtdm,x.fflxtdm,COUNT(distinct x.pcslbm) wts FROM v_pcajxx jb INNER JOIN yx_pc_pcx x ON x.pcslbm = jb.pcslbm INNER JOIN xt_pc_dm d ON x.xtdm=d.dm and d.sfsc = 'N' INNER JOIN xt_pc_dm d1 ON x.flxtdm=d1.dm and d.sfsc = 'N' INNER JOIN xt_pc_dm d2 ON x.fflxtdm=d2.dm and d.sfsc = 'N'
and x.flxtdm = #{flxtdm}
AND x.pcjg != '0'group by x.fflxtdm, x.flxtdm,x.xtdm order by x.fflxtdm, x.flxtdm,x.xtdm ) union all select * from ( SELECT x.xtdm,x.flxtdm,x.fflxtdm,COUNT(distinct x.pcslbm) wts FROM v_pcajxx jb INNER JOIN yx_pc_offline_pcx x ON x.pcslbm = jb.pcslbm INNER JOIN xt_pc_dm d ON x.xtdm=d.dm and d.sfsc = 'N' INNER JOIN xt_pc_dm d1 ON x.flxtdm=d1.dm and d.sfsc = 'N' INNER JOIN xt_pc_dm d2 ON x.fflxtdm=d2.dm and d.sfsc = 'N'
and x.flxtdm = #{flxtdm}
AND x.pcjg != '0' group by x.fflxtdm, x.flxtdm,x.xtdm order by x.fflxtdm, x.flxtdm,x.xtdm ) )group by fflxtdm, flxtdm,xtdm order by fflxtdm,flxtdm,xtdm) aj on ( aj.xtdm = PCX_XTDM.pcxbm and aj.flxtdm = PCX_XTDM.pcxflbm and aj.fflxtdm = PCX_XTDM.pcxflfbm) GROUP BY pcxflfbm,pcxflfmc,pcxflbm,pcxflmc,pcxbm,pcxmc ORDER BY pcxflfbm,pcxflbm,pcxbm

 select * from(
 select PCSLBM,BMSAH,AJMC,AJLBMC, CBDWMC, CBRMC, WCRQ,PCFLBM,CJSJ, CBDWBM，rownum rn from(
 select distinct PCSLBM,BMSAH,AJMC,AJLBMC, CBDWMC, CBRMC, WCRQ,PCFLBM,CJSJ, CBDWBM from(
 select distinct jb.PCSLBM,jb.BMSAH,jb.AJMC,jb.AJLB_MC AJLBMC,jb.BPC_DWMC CBDWMC,jb.PCR_MC CBRMC,jb.BPC_MC BCBRMC,jb.BPC_WCRQ
 WCRQ,jb.PCFLBM,jb.CJSJ,jb.BPC_DWBM CBDWBM from v_pcajxx jb
 left join yx_pc_pcx x on x.pcslbm=jb.pcslbm


     and x.fflxtdm=#{fflxtdm}


     and x.flxtdm=#{flxtdm}


     and x.xtdm=#{xtdm}

 group by jb.pcslbm,jb.PCSLBM,jb.BMSAH,jb.AJMC,jb.AJLB_MC,jb.BPC_DWMC,jb.PCR_MC,jb.BPC_MC,jb.BPC_WCRQ,jb.PCFLBM,CJSJ,jb.BPC_DWBM
 union all
 select distinct jb.PCSLBM,jb.BMSAH,jb.AJMC,jb.AJLB_MC AJLBMC,jb.BPC_DWMC CBDWMC,jb.PCR_MC CBRMC,jb.BPC_MC BCBRMC,jb.BPC_WCRQ WCRQ,
 jb.PCFLBM,CJSJ,jb.BPC_DWBM CBDWBM from v_pcajxx jb
 left join yx_pc_offline_pcx x on x.pcslbm=jb.pcslbm


     and x.fflxtdm=#{fflxtdm}


     and x.flxtdm=#{flxtdm}


     and x.xtdm=#{xtdm}

group by jb.pcslbm,jb.pcslbm,jb.PCSLBM,jb.BMSAH,jb.AJMC,jb.AJLB_MC,jb.BPC_DWMC,jb.PCR_MC,jb.BPC_MC,jb.BPC_WCRQ,jb.PCFLBM,CJSJ,jb.BPC_DWBM
 )) where rownum <= #{row} group by PCSLBM,BMSAH,AJMC,AJLBMC, CBDWMC, CBRMC, WCRQ,PCFLBM,CJSJ, CBDWBM,rownum
 ) where rn>#{page}

select count(distinct pcslbm) num from(
select distinct jb.pcslbm from v_pcajxx jb
left join yx_pc_pcx x on x.pcslbm=jb.pcslbm


    and x.fflxtdm=#{fflxtdm}


    and x.flxtdm=#{flxtdm}


    and x.xtdm=#{xtdm}

 group by jb.pcslbm
union all
select distinct jb.pcslbm from v_pcajxx jb
left join yx_pc_offline_pcx x on x.pcslbm=jb.pcslbm


    and x.fflxtdm=#{fflxtdm}


    and x.flxtdm=#{flxtdm}


    and x.xtdm=#{xtdm}

group by jb.pcslbm
)
