mapper.select:
SELECT wsmb.wsmbbh, wsmb.dwbm, wsmb.pcflbm, wsmb.wsmbmc, wsmb.wsmblb, wsmb.wsmblj, wsmb.wsmbnr, wsmb.wspx, wsmb.spbbm, wsmb.sfgx, wsmb.sm, wsmb.jzmlh, wsmb.sm, '-1' fbm FROM xt_pc_wsmb wsmb WHERE 1= 1
and wsmb.pcflbm = #{pcflbm}
and wsmb.wsmblb = #{wsmblb} order by wsmb.sm asc

mapper.update:
UPDATE yx_pc_sp
SET sprq = sysdate,
spjl = #{spjl},
spyj = #{spyj}
WHERE pcspbm = #{pcspbm}

update  yx_pc_sp
set sfsc = 'Y'
where spwjbm = #{spwjbm}
