values (100003, '检察官办案统计', 100000, 'view', 'Fact_Gg_Ajjbxx', 1, 1, 
        'SELECT cbr,bmmc,bmbm,zs,sl,zb,yb,gd,xh FROM (' || chr(10) || 'SELECT cbr, bmmc, nvl(bmbm,0) bmbm,1 xh,COUNT(1) zs, SUM(nvl(sl,0)) sl,SUM(nvl(zb,0)) zb, SUM(nvl(yb,0)) yb,SUM(nvl(gd,0)) gd' || chr(10) || 'FROM (SELECT ''院领导'' cbr,''院领导'' bmmc FROM dual) bm LEFT JOIN (' || chr(10) || 'SELECT (SELECT MAX(bmbm) FROM XT_ZZJG_BMBM WHERE SFSC = ''N'' AND DWBM ''~dwbm~'' AND SFSC = ''N'' AND BMMC LIKE ''%院领导%'') bmbm,bmsah, DECODE (aj.ajzt,0,1,0)  sl, CASE WHEN aj.BMSAH IS NOT NULL AND aj.bjrq IS NULL AND aj.JSBJRQ IS NULL AND aj.WCRQ IS NULL THEN 1 ELSE 0 END  zb, CASE WHEN aj.BMSAH IS NOT NULL AND (aj.bjrq IS NOT NULL OR aj.JSBJRQ IS NOT NULL) AND aj.WCRQ IS NULL THEN 1 ELSE 0 END  yb, CASE WHEN aj.BMSAH IS NOT NULL AND aj.WCRQ IS NOT NULL THEN 1 ELSE 0 END  gd FROM Fact_Gg_Ajjbxx aj WHERE EXISTS (' || chr(10) || 'SELECT 1 FROM XT_ZZJG_BMBM BM,xt_qx_ryjsfp@tyyw_link.net tjs WHERE bm.dwbm = tjs.dwbm  AND bm.bmbm = tjs.bmbm AND BM.SFSC = ''N'' AND BM.DWBM ''~dwbm~'' AND BM.SFSC = ''N'' AND BM.BMMC LIKE ''%院领导%'' AND aj.cbdw_bm = bm.dwbm AND aj.cbrgh = tjs.gh)' || chr(10) || 'AND EXISTS (SELECT 1 FROM xt_zzjg_rybm@tyyw_link.net ry WHERE ry.dwbm = aj.cbdw_bm AND ry.gh = aj.cbrgh AND ry.sfsc = ''N'' AND ry.sftz = ''N'')' || chr(10) || 'AND EXISTS(SELECT 1 FROM xt_qx_badyryfp@tyyw_link.net fp WHERE fp.badyjsbh IN (1,2) AND fp.dwbm = aj.cbdw_bm AND fp.gh = aj.cbrgh)' || chr(10) || 'AND aj.SFSC = ''N'' AND AJ.CBDW_BM ''~dwbm~'' AND Func_JugementAJLB(AJ.AJLBBM,''1401,1402'') = 0  AND AJ.SLRQ >= TO_DATE(''~startdate~ 00:00:00'',''YYYY-MM-DD HH24:MI:SS'') AND AJ.SLRQ <= TO_DATE(''~enddate~ 23:59:59'',''YYYY-MM-dd HH24:MI:SS'')' || chr(10) || ') tj on 1 = 1' || chr(10) || 'GROUP BY bmbm' || chr(10) || 'UNION ALL' || chr(10) || 'SELECT bmmc cbr,bmmc,bmbm,bmxh xh,COUNT(bmsah) zs,SUM(nvl(sl,0)) sl,SUM(nvl(zb,0)) zb,SUM(nvl(yb,0)) yb,SUM(nvl(gd,0)) gd FROM(' || chr(10) || 'SELECT (SELECT NVL(fbmbm,bm.bmbm) FROM xt_zzjg_bmbm tt WHERE dwbm = bm.dwbm AND tt.bmbm = bm.bmbm) bmbm,(select case when fbmbm is null then bmmc else (select bmmc from xt_zzjg_bmbm t2 where t2.dwbm = t1.dwbm and t2.bmbm = t1.fbmbm ) end from xt_zzjg_bmbm t1 where t1.dwbm = bm.dwbm and t1.bmbm = bm.bmbm ) bmmc,(select case when fbmbm is null then bmxh else (select bmxh from xt_zzjg_bmbm t2 where t2.dwbm = t1.dwbm and t2.bmbm = t1.fbmbm ) end from xt_zzjg_bmbm t1 where t1.dwbm = bm.dwbm and t1.bmbm = bm.bmbm) bmxh,ajfl.bmsah,ajfl.sl,ajfl.zb,ajfl.yb,ajfl.gd  FROM' || chr(10) || '(SELECT dwbm,bmbm,fbmbm,bmmc,bmxh FROM XT_ZZJG_BMBM WHERE dwbm ''~dwbm~'' AND sfsc = ''N'' AND bmmc NOT LIKE ''%院领导%'') bm' || chr(10) || 'LEFT JOIN' || chr(10) || '(SELECT aj.cbdw_bm,aj.cbbm_bm,aj.cbr,aj.cbrgh, aj.bmsah, aj.ajzt,aj.bjrq,aj.jsbjrq,aj.wcrq,DECODE (aj.ajzt,0,1,0)  sl, CASE WHEN aj.BMSAH IS NOT NULL AND aj.bjrq IS NULL AND aj.JSBJRQ IS NULL AND aj.WCRQ IS NULL THEN 1 ELSE 0 END  zb, CASE WHEN aj.BMSAH IS NOT NULL AND (aj.bjrq IS NOT NULL OR aj.JSBJRQ IS NOT NULL) AND aj.WCRQ IS NULL THEN 1 ELSE 0 END  yb, CASE WHEN aj.BMSAH IS NOT NULL AND aj.WCRQ IS NOT NULL THEN 1 ELSE 0 END  gd FROM Fact_Gg_Ajjbxx aj WHERE NOT EXISTS (' || chr(10) || 'SELECT 1 FROM XT_ZZJG_BMBM BM,xt_qx_ryjsfp@tyyw_link.net tjs WHERE bm.dwbm = tjs.dwbm  AND bm.bmbm = tjs.bmbm AND BM.SFSC = ''N'' AND BM.DWBM ''~dwbm~'' AND BM.SFSC = ''N'' AND BM.BMMC LIKE ''%院领导%'' AND aj.cbdw_bm = bm.dwbm AND aj.cbrgh = tjs.gh)' || chr(10) || 'AND EXISTS (SELECT 1 FROM xt_zzjg_rybm@tyyw_link.net ry WHERE ry.dwbm = aj.cbdw_bm AND ry.gh = aj.cbrgh AND ry.sfsc = ''N'' AND ry.sftz = ''N'')' || chr(10) || 'AND EXISTS(SELECT 1 FROM xt_qx_badyryfp@tyyw_link.net fp WHERE fp.badyjsbh IN (1,2) AND fp.dwbm = aj.cbdw_bm AND fp.gh = aj.cbrgh)' || chr(10) || 'AND aj.SFSC = ''N'' AND AJ.CBDW_BM ''~dwbm~'' AND Func_JugementAJLB(AJ.AJLBBM,''1401,1402'') = 0 AND AJ.SLRQ >= TO_DATE(''~startdate~ 00:00:00'',''YYYY-MM-DD HH24:MI:SS'') AND AJ.SLRQ <= TO_DATE(''~enddate~ 23:59:59'',''YYYY-MM-dd HH24:MI:SS'') ) ajfl' || chr(10) || 'ON bm.dwbm = ajfl.cbdw_bm AND bm.bmbm = ajfl.cbbm_bm' || chr(10) || ') GROUP BY bmmc,bmbm,bmxh' || chr(10) || ') ORDER BY xh', 
        null, null, 'cbr', 0, 0, 0, 0, 0, 0, 1, null, null, null, '新增', '编辑', '删除', '筛选', '排序', 1, to_date('22-04-2018 15:41:23', 'dd-mm-yyyy hh24:mi:ss'));
